package module2;
import java.lang.Math;


public class MathDemo {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub

		System.out.println(Math.sqrt(144));
		System.out.println(Math.abs(-66));
		System.out.println(Math.min(144,452));
		System.out.println(Math.max(144,857));
		System.out.println(" Number is "+Math.random());
		double d =(int)(Math.random()*100);
		System.out.println("D is "+d);
		
		System.out.println(Math.round(98.48));
		System.out.println(Math.ceil(98.87));
		System.out.println(Math.floor(145.99));
		System.out.println(Math.pow(3,4));
		
		
	}

}
