package module5;

public class UpwardCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=100;
		double d=a;
		System.out.println(d);
		
		
		double d1 = 68.33;
		
		int i=(int)d1;
		System.out.println(i);
		
		
		float f1 =20.45f;
		
		short s=(short)f1;
		
		System.out.println(s);
		

	}

}
